import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [ruleName, setRuleName] = useState('');
  const [ruleString, setRuleString] = useState('');
  const [message, setMessage] = useState('');
  const [evaluationData, setEvaluationData] = useState({ age: '', department: '' });
  const [evaluationResult, setEvaluationResult] = useState(null);

  // Function to create a new rule
  const createRule = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/rules', {
        name: ruleName,
        ruleString: ruleString
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage('Error creating rule: ' + error.response.data.message);
    }
  };

  // Function to evaluate a rule
  const evaluateRule = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/rules/evaluate', {
        ruleId: 'your-rule-id',  // Replace with a valid rule ID
        data: evaluationData
      });
      setEvaluationResult(response.data.isEligible);
    } catch (error) {
      setEvaluationResult(null);
      setMessage('Error evaluating rule: ' + error.response.data.message);
    }
  };

  return (
    <div>
      <h1>Rule Engine Application</h1>

      <div>
        <h2>Create Rule</h2>
        <input
          type="text"
          placeholder="Rule Name"
          value={ruleName}
          onChange={(e) => setRuleName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Rule String (e.g., age > 30)"
          value={ruleString}
          onChange={(e) => setRuleString(e.target.value)}
        />
        <button onClick={createRule}>Create Rule</button>
      </div>

      <div>
        <h2>Evaluate Rule</h2>
        <input
          type="number"
          placeholder="Age"
          value={evaluationData.age}
          onChange={(e) => setEvaluationData({ ...evaluationData, age: e.target.value })}
        />
        <input
          type="text"
          placeholder="Department"
          value={evaluationData.department}
          onChange={(e) => setEvaluationData({ ...evaluationData, department: e.target.value })}
        />
        <button onClick={evaluateRule}>Evaluate Rule</button>
      </div>

      {message && <p>{message}</p>}
      {evaluationResult !== null && (
        <p>Evaluation Result: {evaluationResult ? 'Eligible' : 'Not Eligible'}</p>
      )}
    </div>
  );
}

export default App;
