import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [ruleName, setRuleName] = useState('');
  const [ruleString, setRuleString] = useState('');
  const [experience, setExperience] = useState('');
  const [message, setMessage] = useState('');
  const [evaluationData, setEvaluationData] = useState({ age: '', department: '', experience: '' });
  const [evaluationResult, setEvaluationResult] = useState(null);

  const createRule = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/rules/create', {
        name: ruleName,
        ruleString: ruleString,
        experience: experience, // Send experience
      });
      setMessage(response.data.message);
      setRuleName('');  // Clear the name input
      setRuleString(''); // Clear the rule string input
      setExperience(''); // Clear the experience input
    } catch (error) {
      if (error.response) {
        setMessage('Error creating rule: ' + (error.response.data.message || error.response.statusText));
      } else if (error.request) {
        setMessage('Error creating rule: No response from server');
      } else {
        setMessage('Error creating rule: ' + error.message);
      }
    }
  };

  const evaluateRule = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/rules/evaluate', {
        ruleId: 'YOUR_RULE_ID_HERE', // Replace with the actual rule ID to evaluate
        data: evaluationData,
      });
      setEvaluationResult(response.data.result);
    } catch (error) {
      setMessage('Error evaluating rule: ' + error.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Rule Engine Application</h1>

      <div style={{ marginBottom: '20px' }}>
        <h2>Create Rule</h2>
        <input
          type="text"
          placeholder="Rule Name"
          value={ruleName}
          onChange={(e) => setRuleName(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Rule String (e.g., age > 30 AND department = 'Sales')"
          value={ruleString}
          onChange={(e) => setRuleString(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="number"
          placeholder="Experience (e.g., 5)"
          value={experience}
          onChange={(e) => setExperience(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <button onClick={createRule}>Create Rule</button>
      </div>

      {message && <p>{message}</p>}

      <div style={{ marginTop: '20px' }}>
        <h2>Evaluate Rule</h2>
        <input
          type="text"
          placeholder="Age"
          value={evaluationData.age}
          onChange={(e) => setEvaluationData({ ...evaluationData, age: e.target.value })}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Department"
          value={evaluationData.department}
          onChange={(e) => setEvaluationData({ ...evaluationData, department: e.target.value })}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Experience"
          value={evaluationData.experience}
          onChange={(e) => setEvaluationData({ ...evaluationData, experience: e.target.value })}
          style={{ marginRight: '10px' }}
        />
        <button onClick={evaluateRule}>Evaluate Rule</button>
        {evaluationResult !== null && (
          <p>Evaluation Result: {evaluationResult ? 'True' : 'False'}</p>
        )}
      </div>
    </div>
  );
}

export default App;
