import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [ruleName, setRuleName] = useState('');
  const [ruleString, setRuleString] = useState('');
  const [message, setMessage] = useState('');
  const [evaluationData, setEvaluationData] = useState({ age: '', department: '', experience: '' });
  const [evaluationResult, setEvaluationResult] = useState(null);
  const [rules, setRules] = useState([]); // To store rules from the database
  const [selectedRuleIds, setSelectedRuleIds] = useState([]); // To store selected rules for combining

  useEffect(() => {
    // Fetch existing rules from the server
    const fetchRules = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/rules');
        setRules(response.data);
      } catch (error) {
        console.error('Error fetching rules:', error);
      }
    };

    fetchRules();
  }, []);

  const createRule = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/rules/create', {
        name: ruleName,
        ruleString: ruleString,
      });
      setMessage(response.data.message);
      setRuleName('');  // Clear the name input
      setRuleString(''); // Clear the rule string input
    } catch (error) {
      if (error.response) {
        setMessage('Error creating rule: ' + (error.response.data.message || error.response.statusText));
      } else if (error.request) {
        setMessage('Error creating rule: No response from server');
      } else {
        setMessage('Error creating rule: ' + error.message);
      }
    }
  };

  const evaluateRule = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/rules/evaluate', {
        ruleId: selectedRuleIds[0], // Use the first selected rule ID for evaluation
        data: evaluationData,
      });
      setEvaluationResult(response.data.result);
    } catch (error) {
      setMessage('Error evaluating rule: ' + error.message);
    }
  };

  const combineRules = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/rules/combine', {
        ruleIds: selectedRuleIds,
      });
      setMessage('Rules combined successfully: ' + JSON.stringify(response.data.combinedRule));
    } catch (error) {
      setMessage('Error combining rules: ' + error.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Rule Engine Application</h1>

      <div style={{ marginBottom: '20px' }}>
        <h2>Create Rule</h2>
        <input
          type="text"
          placeholder="Rule Name"
          value={ruleName}
          onChange={(e) => setRuleName(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Rule String (e.g., age > 30 AND department = 'Sales')"
          value={ruleString}
          onChange={(e) => setRuleString(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <button onClick={createRule}>Create Rule</button>
      </div>

      {message && <p>{message}</p>}

      <div style={{ marginTop: '20px' }}>
        <h2>Evaluate Rule</h2>
        <input
          type="text"
          placeholder="Age"
          value={evaluationData.age}
          onChange={(e) => setEvaluationData({ ...evaluationData, age: e.target.value })}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Department"
          value={evaluationData.department}
          onChange={(e) => setEvaluationData({ ...evaluationData, department: e.target.value })}
          style={{ marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Experience"
          value={evaluationData.experience}
          onChange={(e) => setEvaluationData({ ...evaluationData, experience: e.target.value })}
          style={{ marginRight: '10px' }}
        />
        <button onClick={evaluateRule}>Evaluate Rule</button>
        {evaluationResult !== null && (
          <p>Evaluation Result: {evaluationResult ? 'True' : 'False'}</p>
        )}
      </div>

      <div style={{ marginTop: '20px' }}>
        <h2>Combine Rules</h2>
        <select multiple value={selectedRuleIds} onChange={(e) => setSelectedRuleIds(Array.from(e.target.selectedOptions, option => option.value))}>
          {rules.map(rule => (
            <option key={rule._id} value={rule._id}>{rule.name}</option>
          ))}
        </select>
        <button onClick={combineRules}>Combine Selected Rules</button>
      </div>
    </div>
  );
}

export default App;
